function Game(){this.startHandSize=2,this.maxHandSize=10,this.b=new Board,this.b.initiateBoard(),this.player1=new Player(1),this.player2=new Player(2),this.player1.createDeck(),this.player2.createDeck();for(var t=0;t<this.startHandSize;t++)this.player1.drawCard();for(var t=0;t<this.startHandSize;t++)this.player2.drawCard();$(".box").click(function(){var t=this.getAttribute("x"),a=this.getAttribute("y");this.b.playGame(t,a)}),$("#endTurnButton").click(function(){this.b.endTurn()})}var newGame=new Game;